import React, { useState } from 'react';
import Logo from '../assets/logo.png'; // Update with the correct path to your logo
import { XMarkIcon } from '@heroicons/react/24/outline';

const Header: React.FC = () => {
  const [isOpen, setIsOpen] = useState<boolean>(false);

  const handleLinkClick = () => {
    setIsOpen(false);
  };

  return (
    <header className="fixed w-full bg-white shadow-md z-50 opacity-90">
      <div className="container mx-auto flex justify-between items-center h-16">
        {/* Hamburger Icon */}
        <input
          type="checkbox"
          id="menu-toggle"
          className="hidden"
          checked={isOpen}
          onChange={() => setIsOpen(!isOpen)}
          aria-label="Toggle menu"
        />
        <div
          className="flex flex-col justify-between h-7 w-9 cursor-pointer md:hidden"
          onClick={() => setIsOpen(!isOpen)}
          aria-label="Menu button"
        >
          <span
            className={`block h-1 bg-black rounded transform transition-transform duration-300 ${
              isOpen ? 'rotate-45 translate-y-2.5' : ''
            }`}
          ></span>
          <span
            className={`block h-1 bg-black rounded transition-transform duration-300 ${
              isOpen ? 'scale-0' : ''
            }`}
          ></span>
          <span
            className={`block h-1 bg-black rounded transform transition-transform duration-300 ${
              isOpen ? '-rotate-45 -translate-y-2.5' : ''
            }`}
          ></span>
        </div>

        {/* Logo */}
        <h1 className="text-xl font-bold md:absolute md:top-2.5 md:right-4">
          <img src={Logo} alt="Company Logo" className="h-10" loading="lazy" />
        </h1>

        {/* Menu Items */}
        <ul
          className={`fixed inset-y-0 left-0 transform bg-white shadow-lg md:shadow-none md:relative md:flex md:flex-row md:space-x-6 md:bg-transparent md:translate-x-0 md:h-auto md:w-auto transition-transform duration-300 ease-in-out ${
            isOpen ? 'translate-x-0' : '-translate-x-full'
          }`}
        >
          <li
            className="mt-16 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#home">Home</a>
          </li>
          <li
            className="mt-6 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#aboutus">About Us</a>
          </li>
          <li
            className="mt-6 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#category">Category</a>
          </li>
          <li
            className="mt-6 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#menu">Menu</a>
          </li>
          <li
            className="mt-6 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#testimonials">Testimonials</a>
          </li>
          <li
            className="mt-6 md:mt-0 md:ml-8 text-lg md:text-base text-gray-800 hover:text-teal-600"
            onClick={handleLinkClick}
          >
            <a href="#contact">Contact</a>
          </li>
        </ul>
      </div>

      {/* Overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/40 backdrop-blur-sm z-40 md:hidden"
          onClick={() => setIsOpen(false)}
        ></div>
      )}
    </header>
  );
};

export default Header;
