import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Logo from '../assets/logo.png'; // Update with the correct path to your logo

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [sticky, setSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setSticky(true);
      } else {
        setSticky(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`navbar ${sticky ? 'sticky top-0 z-50' : ''}`}>
      <div className="navbar-container container">
        <div className="logo">
          <img src={Logo} alt="Logo" className="h-16 rounded-full" />
        </div>
        <input type="checkbox" checked={isOpen} onChange={() => setIsOpen(!isOpen)} />
        <div className="hamburger-lines" onClick={() => setIsOpen(!isOpen)}>
          <span className="line line1"></span>
          <span className="line line2"></span>
          <span className="line line3"></span>
        </div>
        <ul className={`menu-items ${isOpen ? 'open' : ''}`}>
          <li><a href="#home">Home</a></li>
          <li><a href="#aboutus">About Us</a></li>
          <li><a href="#history">History</a></li>
          <li><a href="#testimonials">Testimonials</a></li>
          <li>
            <button className="button">Download Now</button>
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
